        {
            <span style="color: #008800; font-weight: bold;">return</span> <span style="color: #0066bb; font-weight: bold;">GetContextProvider</span>().ExpandDynamicLinks(text, resolveSites);
        }

        <span style="color: #008800; font-weight: bold;">public</span> <span style="color: #008800; font-weight: bold;">override</span> UrlOptions <span style="color: #0066bb; font-weight: bold;">GetDefaultUrlOptions</span>()
        {
            <span style="color: #008800; font-weight: bold;">return</span> <span style="color: #0066bb; font-weight: bold;">GetContextProvider</span>().GetDefaultUrlOptions();
        }

